import { useRef, useContext, useState } from 'react';

import AuthContext from '../../store/auth-context';
import classes from './ProfileForm.module.css';

const ProfileForm = () => {
  const newPasswordInputRef = useRef();
  const { token } = useContext(AuthContext);

  const [isLoading, setIsLoading] = useState(false);

  const submitNewPasswordHandler = (event) => {
    event.preventDefault();

    const enteredNewPassword = newPasswordInputRef.current.value;

    setIsLoading(true);

    fetch('https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyCZ31cxrhk2lcF-rD-R2-AT3CJJHICydiE', {
      method: 'POST',
      body: JSON.stringify({
        idToken: token,
        password: enteredNewPassword,
        returnSecureToken: false,
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(res => {
        setIsLoading(false);
        if (res.ok) {
          return res.json();
        } else {
          return res.json().then(data => {
            let errorMessage = 'Password change failed!';
            throw new Error(errorMessage);
          })
        }
      })
      .then(data => {
        console.log(data);
      })
      .catch(err => {
        alert(err.message);
      })
  };

  return (
    <form className={classes.form} onSubmit={submitNewPasswordHandler}>
      <div className={classes.control}>
        <label htmlFor='new-password'>New Password</label>
        <input type='password' id='new-password' ref={newPasswordInputRef} />
      </div>
      <div className={classes.action}>
        <button>Change Password</button>
      </div>
    </form>
  );
}

export default ProfileForm;
